import React,{useEffect,useState} from 'react';
import { Box,colors,TextField,Stack,Button} from '@mui/material';
import Logo from './templates/logo_template.js';
import ShowPages from './templates/showPages.js';
import { proxy } from '../consts/proxy.js';
import "./courseMenu.css"

export default  function CourseMenu (props){

    const [CourseTitle,SetCourseTitle] = useState("");
    const [CourseDescription,SetCourseDescription] = useState("")
    const [Title, setTitle] = React.useState();
    const [TitleError, setTitleError] = React.useState(false);
    const [Description, setDescription] = React.useState();
    const [DescriptionError, setDescriptionError] = React.useState(false);
  
    const handleChangeTitle = (event) => {
        
        setTitle(event.target.value);
    };
    const handleChangeDescription = (event) => {
        
        setDescription(event.target.value);
    };

    function SplitIntoParagraphs(str,divider){
        let res = str.split(divider).map(item => <p>{item}</p>)
        return res
    }
    function SaveBtnOnClick(){

    }

    useEffect(()=>{async function fetch_pages()  {
            
        
            
            let response;
            let values = {"token":sessionStorage.getItem("token"),"course_id":props.courseID,"device":navigator.userAgent.toString(),"_id":sessionStorage.getItem("user_id")}
            try
            {
                response = await proxy.post("/get_pages", JSON.stringify(values, null));
                
                //let data = response?.data;
                let data =[]
                let courses = [];
                /*
                for(let i =0;i<(data['_id']).length;i++){
                    let buff = new Map()
                    buff.set("id",data['_id'][i])
                    buff.set("courseDescription",data['description'][i])
                    buff.set("courseTitle",data['title'][i])
                    buff.set("picture",data['picture'][i])
                    courses.push(buff);
                }*/
                console.log(courses)
                
                
                //SetCourses(courses.map((el)=>{ return <Course  courseText={el.get("courseText")} courseTitle={el.get("courseTitle")} picture={pictures.get(el.get("picture"))} id={el.get("id")}/>}))
                
                
            }
            catch(e)
            {
                alert("Произошла ошибка сервера!\n Мы очень стараемся ей устранить")
                console.log(e);
                //window.location.href = "http://localhost:3000/MainPage"
            }
    
            
          }fetch_pages();}, []);
    if(sessionStorage.getItem("isAdmin")=="true"){
        return(
            <div>
                <Stack direction={'row'} paddingLeft={"44%"}>
                <Logo/>
                </Stack>
                <Box sx={{ paddingLeft:"30%", paddingRight:"30%",alignItems:'center'}}>
                <TextField
                    id="title"
                    label="Заголовок"
                    fullWidth
                    margin="dense"
                    error={TitleError}
                    value={Title}
                    onChange={handleChangeTitle}
                />
                <br/>
                <TextField
                    id="description"
                    label="Описание"
                    error={DescriptionError}
                    multiline
                    fullWidth
                    maxRows={20}
                    value={Description}
                    onChange={handleChangeDescription}
                    margin="dense"
                    
                />
                <br/>
                <br/>
                <Box sx={{paddingLeft:"40%"}}> <Button  variant="contained" onClick={SaveBtnOnClick} >Сохранить</Button>
                <br/>
                
                </Box>
                </Box>
                <Box paddingLeft={"20%"} paddingRight={"20%"}  >
                    <h1>{CourseTitle}</h1>
                    <p className='CourseDescription'>{CourseDescription}</p>
                    <ShowPages courseID={props.courseID}/>
    
                </Box>
    
    
    
            </div>
        );
    }else{
        return(
            <div>
                <Stack direction={'row'} paddingLeft={"44%"}>
                <Logo/>
                </Stack>
    
                <Box paddingLeft={"20%"} paddingRight={"20%"}  >
                    <h1>{CourseTitle}</h1>
                    <p className='CourseDescription'>{CourseDescription}</p>
                    <ShowPages courseID={props.courseID}/>
    
                </Box>
    
    
    
            </div>
        );
    }
    

}
