import React,{useEffect,useState} from 'react';
import { Box,colors,TextField,Stack,Button, unstable_composeClasses} from '@mui/material';
import Logo from './templates/logo_template.js';
import ShowPages from './templates/showPages.js';
import { proxy } from '../consts/proxy.js';
import "./courseMenu.css"

export default  function CourseMenu (props){

    
    const [Pages,SetPages] = useState([])
    const [Title, setTitle] = React.useState();
    const [TitleError, setTitleError] = React.useState(false);
    const [Description, setDescription] = React.useState();
    const [DescriptionError, setDescriptionError] = React.useState(false);
  
    const handleChangeTitle = (event) => {
        
        setTitle(event.target.value);
    };
    const handleChangeDescription = (event) => {
        
        setDescription(event.target.value);
    };

    function SplitIntoParagraphs(str,divider){
        let res = str.split(divider).map(item => <p className='CourseDescription'>{item}<br/></p>)
        return res
    }
    async function SaveBtnOnClick(){
        let response;
            
            let values = {"token":sessionStorage.getItem("token"),"course_id":props.courseID,"device":navigator.userAgent.toString(),"_id":sessionStorage.getItem("user_id"),"title":Title,"description":Description}
            let isFieldsOK=true
            if(Title=="" || Title==undefined) {
                setTitleError(true)
                isFieldsOK=false   
            }
            if(Description=="" || Title==Description) {
                setDescriptionError(true)
                isFieldsOK=false
            }
            if(!isFieldsOK) return;
            try
            {
                response = await proxy.post("/change_course_main_info", JSON.stringify(values, null));
                
                
                let data = response?.data
               
                if(data["status"]===404){
                    alert(data["info"]);
                    document.location.href = "http://localhost:3000/MainPage"
                }
                if(data["status"]===403){
                    alert(data["info"]);
                    sessionStorage.clear()
                    document.location.href = "http://localhost:3000/MainPage"
                }
                
                if(data["status"]===200){
                    alert("Успешная смена")
                    document.location.href = document.location.href 
                }
                
                
            }
            catch(e)
            {
                alert("Произошла ошибка сервера!\n Мы очень стараемся ей устранить")
                console.log(e);
                //window.location.href = "http://localhost:3000/MainPage"
            }
    }
    async function AddPageOnClick(){
        /*  if(request_data["page_type"]=="text_page"):
        courses.update_one({"_id":ObjectId(request_data["course_id"])},{"$push": { "pages": {"title":"title","type":request_data["page_type"],"text":"text"},"$inc:":{"countOfPages":1}} } )
    if(request_data["page_type"]=="video_page"):
        courses.update_one({"_id":ObjectId(request_data["course_id"])},{"$push": { "pages": {"title":"title","type":request_data["page_type"],"_url":"_url"},"$inc:":{"countOfPages":1}} } )
    if(request_data["page_type"]=="question_page_with_one_answer"):
        courses.update_one({"_id":ObjectId(request_data["course_id"])},{"$push": { "pages": {"title":"title","type":request_data["question_page_with_one_answer"],"question":"question","right_answer":"right_answer"},"$inc:":{"countOfPages":1}} } )
    */
    }


    useEffect(()=>{async function fetch_pages()  {
            
        
            
            let response;
            
            let values = {"token":sessionStorage.getItem("token"),"course_id":props.courseID,"device":navigator.userAgent.toString(),"_id":sessionStorage.getItem("user_id")}
            try
            {
                response = await proxy.post("/get_pages", JSON.stringify(values, null));
                
                
                let data = response?.data
               
                if(data["status"]===404){
                    alert(data["info"]);
                    document.location.href = "http://localhost:3000/MainPage"
                }
                if(data["status"]===403){
                    alert(data["info"]);
                    sessionStorage.clear()
                    document.location.href = "http://localhost:3000/MainPage"
                }
                
                setTitle(data["title"]);
                if(sessionStorage.getItem("isAdmin")=="true") {setDescription(data["description"]);}
                else{setDescription(SplitIntoParagraphs(data["description"],"\n"));}
                
                //SetCourses(courses.map((el)=>{ return <Course  courseText={el.get("courseText")} courseTitle={el.get("courseTitle")} picture={pictures.get(el.get("picture"))} id={el.get("id")}/>}))
                
                
            }
            catch(e)
            {
                alert("Произошла ошибка сервера!\n Мы очень стараемся ей устранить")
                console.log(e);
                //window.location.href = "http://localhost:3000/MainPage"
            }
    
            
          }fetch_pages();}, []);
    if(sessionStorage.getItem("isAdmin")=="true"){
        return(
            <div>
                <Stack direction={'row'} paddingLeft={"44%"}>
                <Logo/>
                </Stack>
                <Box sx={{ paddingLeft:"30%", paddingRight:"30%",alignItems:'center'}}>
                    <label>Заголовок</label>
                <TextField
                    id="title"
                    //label="Заголовок"
                    fullWidth
                    margin="dense"
                    error={TitleError}
                    value={Title}
                    onChange={handleChangeTitle}
                />
                <br/>
                <label>Описание</label>
                <TextField
                    id="description"
                    //label="Описание"
                    error={DescriptionError}
                    multiline
                    fullWidth
                    maxRows={20}
                    defaultValue={Description}
                    value={Description}
                    onChange={handleChangeDescription}
                    margin="dense"
                    
                />
                <br/>
                <br/>
                <Box > <Button  variant="contained" onClick={SaveBtnOnClick} >Сохранить</Button>
                <br/>
                </Box>
                </Box>
                <Box paddingLeft={"20%"} paddingRight={"20%"}  >
                <br/>
                <Box sx={{ maxWidth: 200}}>
                <FormControl fullWidth>
                    <InputLabel id="picture_select_label">Логотип</InputLabel>
                    <Select
                    labelId="picture_select_label"
                    id="picture_select"
                    value={Picture}
                    label="Picture"
                    error={PictureError}
                    onChange={handleChangePicture}
                    //defaultValue={10}
                    >
                    
                    <MenuItem value={"def"}>ghfgh</MenuItem>
                    <MenuItem value={"c++"}>ssssssssssssssss</MenuItem>
                    <MenuItem value={"c#"}>dfgfdgfd</MenuItem>
                    
                    
                </Select>
                </FormControl>
                </Box>
                <br/>
                 <Button  variant="contained" onClick={AddPageOnClick} >Добавить</Button>
                <br/>
                
                    <ShowPages courseID={props.courseID} pages={Pages}/>
    
                </Box>
                <br/>
                
                
                <Button  variant="contained" onClick={()=>{document.location.href="http://localhost:3000/MainPage"}} >На главную</Button>
               
    
            </div>
        );
    }else{
        return(
            <div>
                <Stack direction={'row'} paddingLeft={"44%"}>
                <Logo/>
                </Stack>
    
                <Box paddingLeft={"20%"} paddingRight={"20%"}  >
                    <h2>{Title}</h2>
                    <p className='CourseDescription'>{Description}</p>
                    <ShowPages courseID={props.courseID} pages={Pages}/>
                    
                    <br/>
                    <br/>
                    <br/>
                    <Button  variant="contained" onClick={()=>{document.location.href="http://localhost:3000/MainPage"}} >На главную</Button>
    
                </Box>
    
    
    
            </div>
        );
    }
    

}
