                    console.log(e);
                }*/
        }

    }
   
    return(
        <div className='AuthPage'>
            <TextField
                error={EmailError}
                id="Email_Field"
                label="Почта"
                defaultValue={props.UserEMail}
                helperText="Введите новую почту"
                margin="dense"
            />
            <br/>
            <TextField
                error={PasswdError}
                id="passwd"
                label="Пароль"
                type="password"
                helperText="Введите ваш пароль"
                margin="dense"
            />
            <br/>

            {Reg}

            <Checkbox
                id="IsRefCheckBox"
                onChange={handleChangeIsReg}
            />
            <label>Регистрация?</label>
            <br/>
