import React,{useEffect,useState} from 'react';
import { Box,colors,TextField,Button} from '@mui/material';



 
export default  function Auth_reg (props){

    const [EmailError,SetEmailError] = useState(false);
    const [RegPasswd1Error,SetRegPasswd1Error] = useState(false);
    const [RegPasswd2Error,SetRegPasswd2Error] = useState(false);

    const EMAIL_REGEXP = /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/iu;

    const validateEmail = (email) => {
        return EMAIL_REGEXP.test(email)
    };

    let Reg= <div className='AuthPage' >
    <TextField
        error={EmailError}
        id="Email_Field"
        label="Почта"
        defaultValue={props.UserEMail}
        helperText="Введите новую почту"
        margin="dense"
    />
    <br/>
    <TextField
        error={RegPasswd1Error}
        id="reg_passwd_1"
        label="Пароль"
        type="password"
        helperText="Введите ваш пароль"
        margin="dense"
    />
    <br/>
    <TextField
        error={RegPasswd1Error}
        id="reg_passwd_2"
        label="Ещё раз"
        type="password"
        helperText="Повторите ваш пароль"
        margin="dense"
    />
    <br/>
    <Button variant="contained" >Сохранить изменения</Button>
    </div>

   
    return(
        {Reg}
    );
}
