import React,{useEffect,useState} from 'react';
import { Box,TextField,colors } from '@mui/material';

import "./personalAccount.css"
import { common } from '@mui/material/colors';

 
export default  function PersonalAccount (props){

    const [EmailError,SetEmailError] = useState(false);
    const [OldPasswdError,SetOldPasswdError] = useState(false);
    const [NewPasswdError1,SetNewPasswdError1] = useState(false);
    const [NewPasswdError2,SetNewPasswdError2] = useState(false);

    const [EmailVal,SetEmailVal] = useState(props.UserEMail);
   
    const validateEmail = (email) => {
        return String(email)
          .toLowerCase()
          .match(
            /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
          );
      };


    function EmailOnChange(event){
        let email = event.target.value;
        let emailText=event.target.value;
        let countOfDogs = emailText.match(new RegExp("@", 'g'));
        countOfDogs = countOfDogs==null ? 0 : countOfDogs.length
        
        if(countOfDogs>1){
            emailText=emailText.slice(0,email.length-1);
        }

        SetEmailVal(emailText);

        


    }
    return(
        <Box className="PersonalAccount" alignContent={"left"}>
            <h1>Добро пожаловать, рады видеть вас снова!</h1>
            <TextField onChange={EmailOnChange}
                error={EmailError}
                id="Email_Field"
                label="Почта"
                defaultValue={props.UserEMail}
                value={EmailVal}
                helperText="Введите новую почту"
                margin="dense"
            />
            <br/>
            <TextField
                error={OldPasswdError}
                id="old_passwd"
                label="Старый пароль"
                type="password"
                helperText="Введите старый пароль"
                margin="dense"
               
            />
            <br/>
            <TextField
                error={NewPasswdError1}
                id="new_passwd_1"
                label="Новый пароль"
                type="password"
                helperText="Введите новый пароль"
                margin="dense"
            />
            <br/>
            <TextField
                error={NewPasswdError2}
                id="new_passwd_2"
                label="Ещё раз"
                type="password"
                helperText="Повторите новый пароль"
                margin="dense"
            />
            
        </Box>
    );

}
