import React,{useEffect,useState} from 'react';
import { Box,colors,TextField,Button,Select,MenuItem,InputLabel,FormControl } from '@mui/material';
import Course from "./course.js"
import cPlusPicrure from "../../pictures/c++.png"
import cSharpPicture from "../../pictures/cSh.png"
import pythonPicture from '../../pictures/python.png'
import javaPicture from "../../pictures/java.png"
import def from "../../pictures/default.png";
import "./showCourses.css"


export default  function ShowCourses(props){
    const [Courses,SetCourses] = useState(<h1>Что-то пошло не так и курсы не загрузились</h1>);
    const [Picture, setPicture] = React.useState("Стандартная");

    const handleChange = (event) => {
        console.log(event.target.value)
        setPicture(event.target.value);
    };
    const [AdminMenu,SetAdminMenu] = useState(sessionStorage.getItem("isAdmin")=="true" ? <div className='adminMenu'>
                
                <FormControl fullWidth>
                    <InputLabel id="picture_select_label">Логотип</InputLabel>
                    <Select
                    labelId="picture_select_label"
                    id="demo-simple-select"
                    value={Picture}
                    label="Picture"
                    onChange={handleChange}
                    defaultValue='Стандартная'
                    >
                    <MenuItem value={10}>Стандартная</MenuItem>
                    <MenuItem value={20}>С++</MenuItem>
                    <MenuItem value={30}>С#</MenuItem>
                    <MenuItem value={40}>java</MenuItem>
                    <MenuItem value={50}>python</MenuItem>
                    
                </Select>
                </FormControl>

                <TextField
                    id="Title"
                    label="Заголовок"
                    fullWidth
                    margin="dense"
                />
                <br/>
                <TextField
                    id="description"
                    label="Описание"
                    multiline
                    fullWidth
                    maxRows={20}
                    margin="dense"
                    
                />
                <br/>
                <br/>
                <Button  variant="contained" onClick={SaveBtnOnClick} >Сохранить</Button>
                <br/>
                <br/>


    </div>:<></>)

    function SaveBtnOnClick(){

    }
    let arr = [];
    let first = new Map()
    first.set("id",26)
    first.set("courseDescription","sdhtrehf")
    first.set("courseTitle","dfrjtfg")
    first.set("picture",def)
    arr.push(first)
    

    let buff = arr.map((el)=>{return <Course  courseText={el.get("courseText")} courseTitle={el.get("courseTitle")} picture={el.get("picture")} id={el.get("id")}   />})

    

    return (
        <div>
            {AdminMenu}

            {buff}  
          
            <Course courseDescription="Здесь мог бы быть ваш курс" courseTitle="Ура пиво" picture={pythonPicture} id={1}/>
            <Course courseDescription="Здесь мог бы быть ваш курс" courseTitle="Ура пиво" picture={javaPicture} id={2}/>

            
        </div>
    )
}
